	; JB demo music, probably made for Maniacs of Noise	; Converted from TurboAssembler to ACME by dmx87	!to "Bjerregaard_J_James_Bond_3.sid",plain	* = $0000		!text "PSID"	!be16 2							; version 2	!be16 $7c						; data offset	!be16 $1000							; Load (0 = auto)	!be16 SETMUS						; Init	!be16 PLAY						; Play	!be16 2							; num songs	!be16 1							; first song	!word 0	!word 0-	!text "James Bond 3 Demo"	!fill 32 - (* - -)-	!text "Johannes Bjerregaard"	!fill 32 - (* - -)-	!text "1989 Johannes Bjerregaard"	!fill 32 - (* - -)	!be16 $0014							; v2 flags	!be16 0							; Start page, page length (reloc)	!be16 0							; Reserved!pseudopc $1000 {;---------------------------------------ZP       = $FC;(ZP+1 ALSO USED);---------------------------------------INITMUS  = 0         ;IMPROVEMENTS:         ;------------         ;"2ND SUSTAIN"         ;SET1+7 BIT 4-7 = LEVEL         ;RELEASE ONLY WHILE RESTING         ;(!)CHIP RESET ALWAYS         ;AUTO SUSTAIN DELAY         ;SET1+6 BIT 5-7 = TIME=FRAMES         ;RELEASE TRIGGER         ;SET1+3 BIT 4-7 = 2*TIME=FRAMES         ;DON'T FORGET:         ;FILTER SEQUENCE END/STOP         ;RECOGNITION BYTES CHANGED!;---------------------------------------         ;RECEIVED        : 18/6         ;LATEST DELIVERY : 19/6         ;ROOM            : ...         ;ZERO PAGE       : ...         ;SPECIFICATIONS  : 30 SECS OF         ;                  JAMES BOND         ;                  TITLE TUNE         ;                  INCLUDING         ;                  A DEMO OF         ;                  THE MANIACS         ;                  OF NOISE         ;                  MUSIC STYLE;---------------------------------------        ; *= $0801        ; !by $21,$08,$C5,$07,$9E,$32        ; !by $30,$38,$33,$20,$53,$A4        ; !by $52,$4D,$20,$4C,$B0,$44        ; !by $20,$4D,$55,$53,$49,$43        ; !by $20,$28,$43,$29,$4D,$91        ; !by $2E,$00,$00,$00;         JMP DEMO         JMP SETMUS;INIT(.A=MUSIC NO)         JMP MUSOFF;STOPS PLAYING TUNE         JMP PLAY;JSR IN IRQ-INTERRUPTFADERATE !by 0;STORE FADE RATE(1-127)                ;HERE.....PLAYMUSON    = *+1         LDA #0         BNE PLAYIT         RTSPLAYIT   LDX #2         DEC TEMPOCNTNEXTV         LDA TEMPOCNT         BPL DOPETEMPOSET = *+1         LDA #2         STA TEMPOCNT         DEC LEN         DEC LEN+1         DEC LEN+2DOPE     LDA LEN,X         BPL DONOT         LDA TEMPOCNT         CMP #1         BEQ READVALS         BCS DONOT         JMP SETVALSDONOT    JMP COMPUTE;---------------------------------------READVALS         TXA         ASL         TAY         LDA MUSSTART,Y         STA ZP         LDA MUSSTART+1,Y         STA ZP+1         LDY SEQNO,XREREAD   LDA (ZP),Y;READ SEQUENCE NO.         BPL LESS         CMP #$FF         BNE NOTREP         LDY #0         STY SEQNO         STY SEQNO+1         STY SEQNO+2         LDA #$00         STY NEWRELOFF         STY NEWRELOFF+1         STY NEWRELOFF+2         JMP REREADNOTREP   CMP #$FE         BNE NOTEND;---------------------------------------MUSOFF   LDA #0         STA MUSON         STA $D404         STA $D404+7         STA $D404+14         RTS;---------------------------------------NOTEND   AND #$7F         SEC         SBC #$40         STA TRANSP,X         INY         INC SEQNO,X         JMP REREAD;---------------------------------------LESS     TAY         ;READ SEQUENCE DATA         LDA LOSEQ,Y         STA ZP         LDA HISEQ,Y         STA ZP+1         LDA GATE,X         AND #$FE         STA GATE,X         LDY SEQPTR,XREREAD2  LDA (ZP),Y         CMP #$60         BCS DYTT         JMP NOTTDYTT     CMP #$80;$60-$7F=ARPEGGIO         BCS NOTARP2         AND #$1F         STA ARPSELECT,X         INY         JMP REREAD2NOTARP2  CMP #$FC;TIE NEXT TWO NOTES         BNE NOTARP         DEC NOPCOUNTER,X         LDA #$FE         STA GATE,X         INY         JMP REREAD2NOTARP   CMP #$E0         BCC NOTPAUSE         AND #$1F         STA LEN,X         INY         TYA         STA SEQPTR,X         LDA LAST,X         BNE DONTSET         LDY ENVPTR,X         LDA SET1+7,Y         AND #$F0         ORA RELEASE,X         STA RELEASE,X         LDY D4POINT,X         LDA WFM,X         AND #$FE         STA $D404,Y         LDA #0         STA $D405,Y         LDA RELEASE,X         STA $D406,Y         LDA #$FE         STA GATE,X         DEC WFPCOUNTER,X         LDA #0         STA NEWRELOFF,X         LDA #1         STA LAST,XDONTSET         LDY SEQPTR,X         LDA (ZP),Y         CMP #$FF         BNE JCMP2         INC SEQNO,X         LDA #0         STA SEQPTR,XJCMP2         JMP SETSIDNOTPAUSE CMP #$C0         BCC NOTLEN         AND #$1F         STA SETLEN,X         STA NLEN,X         INY         JMP REREAD2NOTLEN         PHA         LDA SETLEN,X         STA NLEN,X         PLA         CMP #$A0         BCC NOTGLIDE         AND #$1F         STA GLIDE,X         INY         LDA (ZP),Y         CLC         ADC TRANSP,X         STA ENDNOTE,X         INY         JMP REREAD2NOTGLIDE ;THIS GOTTA BE $80-$9F         AND #$1F         ASL         ASL         ASL         STA ENVPTR,X         STY TEMP         TAY         LDA SET1+4,Y         AND #$1F         STA ARPSELECT,X         LDY TEMP         INY         JMP REREAD2READ2    INY         LDA (ZP),YNOTT     CLC         ADC TRANSP,X         STA NOTE,X         INY         TYA         STA SEQPTR,X         LDA (ZP),Y         CMP #$FF         BNE WHI2         LDA #0         STA SEQPTR,X         INC SEQNO,XWHI2         LDA GATE,X         AND #1         BNE WHI         LDY D4POINT,X         LDA #$F0         STA $D404,Y         STA WFM,XWHI         JMP AZ;------------------------------------SETVALS         LDY ENVPTR,X         STY TEMP2         LDA SET1+2,Y         AND #$0F         LSR         STA VIBDIR,X;VIBRATO DIRECTION         LDA SET1+1,Y         AND #$0F         STA RELEASE,X         LDA NLEN,X         STA LEN,X         LDA #0         STA VIBRATE,X         STA VIBRATEHI,X         STA LAST,X         LDA GATE,X         AND #$01         BEQ BURP         JMP COMPUTEBURP     STX TEMP         STA RELCTR,X         STA PWTIMES,X         STA FTMS,X         LDA #$80         STA NEWRELOFF,X         LDA D4POINT,X         LDY TEMP2         TAX         LDA #0         STA $D404,X         LDA SET1,Y         STA $D405,X         LDA SET1+1,Y         STA $D406,X         LDX TEMP         LDA #9;BYTE 0 = REPEAT POINT         STA WFM,X         LDA #1         STA VIBCOUNTER,X;VIB(DELAY)         STA WFPCOUNTER,X;WAVEFORM         STA NOPCOUNTER,X;ARPEGGIO         LDA #2;BYTE 1=TRIG VALUE         STA PWPCOUNTER,X;PULSE WIDTH         LDA #3         STA FIPCOUNTER,X;SET+D418         LDY TEMP2         LDA #$FF         STA GATE,X         LDA SET1+3,Y         AND #$0F         TAY         LDA PUTABLO,Y         STA FIX         LDA PUTABHI,Y         STA FIX+1         LDY #1FIX      = *+1         LDA $1234,Y         STA LOPW,X         AND #$0F         STA HIPW,X         LDY ENVPTR,X         LDA SET1+5,Y         AND #$0F         TAY         LDA FITABLO,Y         STA ZP         LDA FITABHI,Y         STA ZP+1         LDY #1         LDA (ZP),Y         BEQ FILTOFF         STA CTOF         INY         LDA (ZP),Y         STA D418         LDA D417         ORA FONT,X         STA D417         JMP GUFFILTOFF  LDA D417         AND FOFFT,X         STA D417GUF      JMP SETSID;---------------------------------------COMPUTE         ;WAVEFORMS         LDY ENVPTR,X         LDA SET1+6,Y         AND #$0F         TAY         LDA WFTABLO,Y         STA ZP         LDA WFTABHI,Y         STA ZP+1         LDY WFPCOUNTER,XRE       LDA (ZP),Y         CMP #$FE         BCC WAVEFORM         BEQ NOCHANG;LAST WF REACHED         LDY #0         LDA (ZP),Y         TAY         JMP REWAVEFORM STA WFM,X         INY         TYA         STA WFPCOUNTER,X;---------------------------------------NOCHANG         ;ARPEGGIO (SET NOTES)         LDY ARPSELECT,X         LDA NOTABLO,Y         STA ZP         LDA NOTABHI,Y         STA ZP+1         LDY #0         LDA (ZP),Y         STA TMP1         LDY NOPCOUNTER,XRE2      LDA (ZP),Y         CMP #$FE         BCC NOTEOK         BEQ VIBRATO         LDY #0         LDA (ZP),Y         AND #$7F         TAY         JMP RE2NOTEOK         BIT TMP1         BPL NOTESET         STA HIFQ,X         LDA #0         STA LOFQ,X         INY         TYA         JMP WACKNOTESET         CMP #$80         AND #$7F         BCS STEADY         CLC         ADC NOTE,XSTEADY   INY         STY TEMP         STA TEMPNOTE,X         TAY         LDA FQDATLO,Y         STA LOFQ,X         LDA FQDATHI,Y         STA HIFQ,X         LDA TEMPWACK         STA NOPCOUNTER,X         JMP NOVIBR;---------------------------------------VIBRATO         LDA GLIDE,X         BNE NOTVIB         LDY ENVPTR,X         LDA SET1+2,Y         AND #$F0;DELAY         LSR         LSR         LSR         CMP VIBCOUNTER,X         BCC VIBRNOTVIB   JMP NOVIBRVIBR         LDY TEMPNOTE,X         LDA FQDATLO,Y         SEC         SBC FQDATLO-1,Y         LDA FQDATHI,Y         SBC FQDATHI-1,Y         CLC         ADC #1         STA ZP         LDY ENVPTR,X         LDA SET1+7,Y         AND #$0F         TAY ;VIBRATO RATE         LDA ZPROLLOVER DEY         BMI ENOUGH         ASL         JMP ROLLOVERENOUGH   STA ZP         LDY ENVPTR,X         LDA SET1+2,Y         AND #$0F         LSR         CMP VIBDIR,X         BCS VIBDOWN         LDA LOFQ,X         CLC         ADC VIBRATE,X         STA LOFQ,X         LDA HIFQ,X         ADC VIBRATEHI,X         STA HIFQ,X         JMP SETRATEVIBDOWN  LDA LOFQ,X         SEC         SBC VIBRATE,X         STA LOFQ,X         LDA HIFQ,X         SBC VIBRATEHI,X         STA HIFQ,XSETRATE  LDA SET1+5,Y         AND #$F0         LSR         LSR         CMP VIBCOUNTER,X         BCC DIRINC         LDA ZP         CLC         ADC VIBRATE,X         STA VIBRATE,X         LDA VIBRATEHI,X         ADC #0         STA VIBRATEHI,XDIRINC   INC VIBDIR,X         LDA SET1+2,Y         AND #$0F         CMP VIBDIR,X         BCS NOVIBR         LDA #0         STA VIBDIR,X;---------------------------------------NOVIBR   ;PULSE WIDTH PROGRAMMING         LDY ENVPTR,X         LDA SET1+3,Y         AND #$0F         TAY         LDA PUTABLO,Y         STA ZP         LDA PUTABHI,Y         STA ZP+1         LDY PWPCOUNTER,X         LDA PWTIMES,X         BNE STORESAMEREREAD4  LDA (ZP),Y         CMP #$FE         BCC VALUEOK         BEQ NOMOREPW         LDY #0         LDA (ZP),Y         TAY         JMP REREAD4STORESAME         LDA PWVALUE,XVALUEOK  STA PWVALUE,X         CMP #$80         AND #$7F         BCS ADD         ASL         STA TEMP         LDA LOPW,X         SEC         SBC TEMP         STA LOPW,X         LDA HIPW,X         SBC #0         STA HIPW,XGETTIMES LDA PWTIMES,X         BNE DECPWT         INY         LDA (ZP),Y         STA PWTIMES,X         INY         TYA         STA PWPCOUNTER,X         JMP NOMOREPWADD      ASL         CLC         ADC LOPW,X         STA LOPW,X         LDA HIPW,X         ADC #0         STA HIPW,X         JMP GETTIMESDECPWT   DEC PWTIMES,X;---------------------------------------NOMOREPW ;FILTER PROGRAMMING         LDY ENVPTR,X         LDA SET1+5,Y         AND #$07         TAY         LDA FITABLO,Y         STA ZP         LDA FITABHI,Y         STA ZP+1         LDY FIPCOUNTER,X         LDA FTMS,X         BNE READSAMERRREAD   LDA (ZP),Y         CMP #$7E         BEQ NOFILTCHANGE         CMP #$7F         BNE ADDSUBOK         LDY #0         LDA (ZP),Y         STA FIPCOUNTER,X         TAY         JMP RRREADREADSAME LDA FDTA,XADDSUBOK STA FDTA,X         LDA CTOF         CLC         ADC FDTA,X         STA CTOF         LDA FTMS,X         BNE NOTIMESET         INY         LDA (ZP),Y         STA FTMS,X         INY         TYA         STA FIPCOUNTER,XNOTIMESET DEC FTMS,X;---------------------------------------NOFILTCHANGE         ;GLIDE         LDA GLIDE,X         BNE GLOK         JMP ZDGLOK     LDY ENDNOTE,X         LDA FQDATLO,Y         STA ZP         LDA FQDATHI,Y         STA ZP+1         LDA #0         STA TEMP2         LDA #$0F         LDY GLIDE,XLOOPING  DEY         BMI NOMOREOFTHAT         ASL         ROL TEMP2         JMP LOOPINGNOMOREOFTHAT         STA TEMP         LDA ENDNOTE,X         CMP TEMPNOTE,X         BCS GLIDEUP         LDA LOFQ,X         SEC         SBC TEMP         STA LOFQ,X         LDA HIFQ,X         SBC TEMP2         STA HIFQ,X         LDA LOFQ,X         SEC         SBC ZP         LDA HIFQ,X         SBC ZP+1         BCS ZDGLIDEND  LDA #0         STA GLIDE,X         LDA ZP         STA LOFQ,X         LDA ZP+1         STA HIFQ,X         JMP ZDGLIDEUP         LDA LOFQ,X         CLC         ADC TEMP         STA LOFQ,X         LDA HIFQ,X         ADC TEMP2         STA HIFQ,X         LDA ZP         SEC         SBC LOFQ,X         LDA ZP+1         SBC HIFQ,X         BCS ZD         JMP GLIDEND;---------------------------------------ZDZ3       INC VIBCOUNTER,X         LDY ENVPTR,X         LDA SET1+6,Y         AND #%11100000         BEQ NOWAYPAL         LSR         LSR         LSR         LSR         LSR         CMP RELCTR,X         BNE NOWAYPAL         LDA SET1+7,Y         AND #$F0         ORA RELEASE,X         STA RELEASE,X         LDY D4POINT,X         LDA WFM,X         AND #$FE         STA $D404,Y         LDA #0         STA $D405,Y         LDA RELEASE,X         STA $D406,Y         LDA #0         STA NEWRELOFF,XNOWAYPAL         INC RELCTR,X;---------------------------------------SETSID         LDA NEWRELOFF,X         BMI WEG2         LDY ENVPTR,X         LDA SET1+3,Y         AND #$F0         LSR         LSR         LSR         CMP NEWRELOFF,X         BCS WEG         LDA #$FE         STA GATE,XWEG         INC NEWRELOFF,XWEG2         LDY D4POINT,X         LDA WFM,X         AND GATE,X         STA $D404,Y         LDA LOFQ,X         STA $D400,Y         LDA HIFQ,X         STA $D401,Y         LDA LOPW,X         STA $D402,Y         LDA HIPW,X         STA $D403,YAZ         LDA LEN,X         BPL Z2         LDA TEMPOCNT         CMP #$02         BNE Z2         LDY D4POINT,X         LDA #$00         STA $D406,Y         STA $D405,YZ2       DEX         BMI SETVOL         JMP NEXTVSETVOL         LDA D417         STA $D417         LDA CTOF         STA $D416         LDA FADERATE         BEQ STOREIT         DEC FADECOUNTER         BPL STOREIT         LDA VOLUME         BEQ STOREIT         LDA FADERATE         STA FADECOUNTER         DEC VOLUMESTOREIT  LDA D418         ORA VOLUME         STA $D418         LDA VOLUME         BNE OUTAHERE         JMP MUSOFFOUTAHERE RTS;---------------------------------------TEMPOCNT !by 0SETLEN   !by 0,0,0TRANSP   !by 0,0,0NONEW    !by 0,0,0SEQNO    !by 0,0,0SEQPTR   !by 0,0,0GLIDE    !by 0,0,0NLEN     !by 0,0,0D4POINT  !by 0,7,14TEMP     !by 0TEMP2    !by 0ENDNOTE  !by 0,0,0ENVPTR   !by 0,0,0VIBCOUNTER !by 0,0,0FIPCOUNTER !by 0,0,0WFPCOUNTER !by 0,0,0NOPCOUNTER !by 0,0,0PWPCOUNTER !by 0,0,0VIBDIR   !by 0,0,0WFM      !by 0,0,0GATE     !by 0,0,0LEN      !by 0,0,0LOFQ     !by 0,0,0HIFQ     !by 0,0,0TEMPNOTE !by 0,0,0VIBRATE  !by 0,0,0VIBRATEHI !by 0,0,0NOTE     !by 0,0,0LOPW     !by 0,0,0HIPW